using System.ComponentModel.DataAnnotations;using System.Text.Json.Serialization;namespace Backend.Modules.Master.Common.Country{    public class CountryCreateUpdateDto    {        [Required(ErrorMessage = "Country Code is required !")]        public string CountryCode { get; set; }        [Required(ErrorMessage = "Country Name is required !")]        public string CountryName { get; set; }        public string? Continent { get; set; }              public string? Capital { get; set; }                public string Currency { get; set; }        [Required]        [MaxLength(1,ErrorMessage ="Is Active max length 1 ")]        public string IsActive { get; set; }        public string? CreatedBy { get; set; } = null;        [JsonIgnore]        public string? CreatedOn { get; set; } = null;              public string? ModifiedBy { get; set; } = null;        [JsonIgnore]        public string? ModifiedOn { get; set; } = null;    }}